	    import oracle.odi.mapping.generation.AbstractSyntaxTree.ASTProducerMethod;
		import oracle.odi.domain.mapping.generator.KMGeneratorDelegateRegistry.GeneratorDelegateClass;
	    @GeneratorDelegateClass(componentType="AP", descriptionKey="GeneratorDelegateClass.LKMFileToHive")
        public class LKM_File_to_Hive_LOAD_DATA extends HiveBaseLKM {

    	    // class constructor; initialization is done in setBinding method
            public LKM_File_to_Hive_LOAD_DATA() {}      

            // Declare KM level vars
            protected boolean option_EXTERNAL_TABLE
            protected boolean option_STOP_ON_FILE_NOT_FOUND
            protected boolean option_FILE_IS_LOCAL
            //protected HiveCreateTableStatement stgTableAST
            //protected FileToHiveStatement stgAST

    	public void setBinding( Binding binding ) {
        	super.setBinding( binding )
            // Initialize variables
            option_EXTERNAL_TABLE         = isOptionTrue( "EXTERNAL_TABLE" )
            option_STOP_ON_FILE_NOT_FOUND = isOptionTrue( "STOP_ON_FILE_NOT_FOUND" )
            option_FILE_IS_LOCAL          = isOptionTrue( "FILE_IS_LOCAL" )
        }

        void updateBinding() {
        	super.updateBinding()
            // Publish variables into binding to make them available in templates and variableDefText
            mBinding.setVariable( "option_EXTERNAL_TABLE",         option_EXTERNAL_TABLE ) 
            mBinding.setVariable( "option_STOP_ON_FILE_NOT_FOUND", option_STOP_ON_FILE_NOT_FOUND ) 
            mBinding.setVariable( "option_FILE_IS_LOCAL",          option_FILE_IS_LOCAL )
            //mBinding.setVariable( "stgTableAST", stgTableAST )
            //mBinding.setVariable( "stgAST", stgAST )    
        }

        AbstractSyntaxTree getHiveLoadDataCommand( HiveTable table, SqlQuery input ) {
            def compKM  = generatorContext.getComponentKM(); //compKM.getName()            
            def ast

            // Is this a transparent target load?
            if ( physicalNode.isDirectLoad() ) {

            	//TODO: for transparent LKMs: we have to validate tighter constraints: e.g. all target columns must be mapped from source expression except for partition columns. 
            	//These can also be filled with constant value or file name fragments

                def tgtCols    = new ArrayList<Column>();
                def selectList = new ArrayList<ISelectItem>();    	
                def query = getSourceAndTargetExpressionsAndQuery( table, input, selectList, tgtCols, 
                    { attr -> 
                    	// for direct LKMs all MapPhysicalColumns should be mapped. 
                    	// So we error out, if any unmapped column:
                    	// As long as we do rowformat handling without a dedicated component for proper input/output signature handling,
                    	// we have to tolerate unmapped target columns!
                    	//if ( attr.getExpression() == null ) 
                    	    //TODO: use correct expression
                    	    //throw new RuntimeException( """Column "${attr.getName()}" is not mapped. For direct loading all columns must be mapped.""" )
                    	// Otherwise just copy the expression over
                    	return new CopyColumnExpression()
                    }  )                  
                ast     = new FileToHiveStatement( physicalNode, table, tgtCols, query, ffFinder, option_FILE_IS_LOCAL, false )

            } else {

            	// No, this is a standard LKM node. So we use the node's physical columns            	                	                                                              
                def tgtCols    = new ArrayList<Column>();
                def selectList = new ArrayList<ISelectItem>();    	
                def query = getSourceAndTargetExpressionsAndQuery( table, input, selectList, tgtCols, 
                    { attr -> // for LKMs all MapPhysicalColumns are mapped. So we can just copy all columns:
                    	return new CopyColumnExpression()
                    }  )                  
                ast     = new FileToHiveStatement( physicalNode, table, tgtCols, query, ffFinder, option_FILE_IS_LOCAL, false)                
            }

            def srcFile = ast.getSourceFileDataStore()
            def rowFormatClause = ""            
            if ( getOption("OVERRIDE_ROW_FORMAT").trim().length() > 0 ) { 
                rowFormatClause   = getOption("OVERRIDE_ROW_FORMAT")
            } else {
                if ( srcFile.isDelimited() ) {
                    rowFormatClause  = """row format delimited\n"""+
                                           """fields terminated by '${srcFile.getJavaEscapedFieldSeparator()}' \n"""+
                                           """lines terminated by '${srcFile.getJavaEscapedRowSeparator()}' \n"""+
                                           """stored as textfile\n"""
                } else { /* Fixed length file format */ 
                    rowFormatClause  = """row format serde 'org.apache.hadoop.hive.contrib.serde2.RegexSerDe' \n"""+
                                       """with serdeproperties ( \n"""+
                                       """  "input.regex" = "${srcFile.getColumns().collect { col -> "(.{"+col.getFileDescriptor().getBytes()+"})" }
                                       	                                           .join("")}", \n"""+
                                       """  "output.format.string" = "" \n"""+
                                       """) \n"""+
                                       """stored as textfile\n"""
                } 
            }            
            if ( option_EXTERNAL_TABLE )
                rowFormatClause += """\nLOCATION '<?=odiRef.getInfo("SRC_SCHEMA")?>' \n"""
            table.external = option_EXTERNAL_TABLE 
            table.rowFormatClause = rowFormatClause

            //ast.addTemplateVarItem( "AST", ast )
            //ast.setLineType( IProcedureLine.LineType.EX_UNIT_MAIN )
            ast.setTargetTechnology( getGroovyTechnology() )
            return ast
        }

  		@ASTProducerMethod(processingType=ProcessingType.SOURCE, producedASTType=SqlQuery.class, styleKey="ProcessingStyleKey.File.SELECT")  
        public AbstractSyntaxTree SELECT() {
            // Copied from oracle.odi.domain.mapping.component.km.SQLFileCompKM"
            SqlQuery result = new SqlQuery( physicalNode.getTemplateUtils() )
            //result.setUseSubselectForMultiJoin(true)
            def tableName = physicalNode.getBoundObjectName();  // The name of the associated datastore object
            def tableQualifier = physicalNode.getLocation() == null ? null : physicalNode.getLocation().getName();
            def tableAlias = generatorContext.generateUniqueAlias(physicalNode);
            //String customSrcText = OdiRef.getOdiGeneratedAccessName("SOURCE_NAME", physicalNode);
            String customSrcText = generatorContext.getAPNodeCollTableAccessName()
            Table srcTable = new Table( tableName, tableQualifier, customSrcText );

            srcTable.setModelTable(physicalNode.getLogicalComponent());
            FromClause from = new FromClause(new TableReference(srcTable), tableAlias);
            List<FromClause> fromList = new ArrayList<FromClause>();
            fromList.add(from);
            // add source location for multi IKM usage like getInfo('SRC_TECHNO_NAME')
            if (physicalNode.getLocation() != null) {
                result.setSourceLocation(physicalNode.getLocation());
            }
            //def selectExprList = component.getAttributes().findAll { attr -> return attr.isReferenced() }
            //                                              .collect { attr -> new StringExpression( attr.getSQLAccessName(tableAlias), attr, attr ) }     
            def selectExprList = physicalNode.getPhysicalColumns().collect { col -> new StringExpression( col.getSQLAccessName(tableAlias), col, col.getRefObject() ) }     
            result.setSelectList( selectExprList );
            result.setFromList(fromList);
            return result;
        }

		@ASTProducerMethod(processingType=ProcessingType.TARGET, producedASTType=HiveInsertStatement.class, styleKey="ProcessingStyleKey.Hive.INSERT")
        AbstractSyntaxTree INSERT( SqlQuery input ) {         
          // Find source DataStore
          def srcLst = input.getFromList().collect { it.getSourceTables().collect { it } }.flatten()
          if ( srcLst.size() != 1 )
              throw new RuntimeException( "Input query must have exactly one source file." )
          def sourceFile = srcLst[0]
          def sourceDataStore   = sourceFile.getModelTable().getBoundDataStore()
          def isFixedLengthFile = (sourceDataStore.getFileDescriptor().getFormat() == OdiDataStore.FileDescriptor.Format.FIXED)

          // Create DDL for staging table
          def stagingTable = getStagingTable()
          def stgTableColumnList = sourceDataStore.getColumns( ffFinder ).findAll { col -> !col.isPartition() }
                                                  .collect { col -> def c = new Column( stagingTable, col.getName(), col )
                                                  	                // For fixed length input files all columns must be of type STRING
                                                  	                if ( isFixedLengthFile ) c.setDdlDataType( "STRING" )
                                                  	                return c }                 
          stgTableAST = new HiveCreateTableStatement( "abc", "def", stagingTable, getHiveTechnology(), ffFinder )
          stgTableAST.setColumns( stgTableColumnList )                
          //TODO: How to set the task name to "Load data file(s)"?
          stgAST = getHiveLoadDataCommand( stagingTable, input )

          // When this is an external table, we do not need to explicitly load the data files
          if ( option_EXTERNAL_TABLE ) 
              stgAST.skipGenerateTask()

          // Publish variables into binding to make them available in templates and variableDefText
          updateBinding()          
          return stgAST
        }      
        } // End of LKM class
