        import oracle.odi.mapping.generation.AbstractSyntaxTree.ASTProducerMethod;
        import oracle.odi.domain.mapping.generator.KMGeneratorDelegateRegistry.GeneratorDelegateClass;
        import oracle.odi.km.exception.OdiKMException;

        @GeneratorDelegateClass(componentType="AP", descriptionKey="GeneratorDelegateClass.LKMHiveToSql_SQOOP")
        public class LKM_Hive_to_SQL_SQOOP extends HiveSqoopExportBaseLKM {
            protected SqlQuery sourceQuery
            protected String sourceQueryLSchema

            // class constructor; initialization is done in setBinding method
            public LKM_Hive_to_SQL_SQOOP() {}

            // Declare KM level vars        
            protected boolean  option_DELETE_TEMPORARY_OBJECTS

            public void setBinding( Binding binding ) {
                super.setBinding( binding )
                // Initialize variables
                option_DELETE_TEMPORARY_OBJECTS = isOptionTrue( "DELETE_TEMPORARY_OBJECTS" )
            }

            void updateBinding() {
                super.updateBinding()
                // Publish variables into binding to make them available in templates and variableDefText
                mBinding.setVariable( "DELETE_TEMPORARY_OBJECTS", option_DELETE_TEMPORARY_OBJECTS )     
                // We need Hive session setup
                prepareHiveSession = true
            }

            void addHiveSourceTask( String kmName, String taskName, String templateText, int order, String lineType, String lSchemaName ) {
                // Add Hive source task
                def aAST =  new CustomAST( kmName, templateText )                
                aAST.setIsPushFromSource(true)
                aAST.setOrder( order )
                aAST.setSourceTechnology( getHiveTechnology() )

                // Can we use mapping's standard Hive LSchema? ("LKM Hive to SQL SQOOP")
                if ( lSchemaName == null) {
                    aAST.setSourceLocation( physicalNode.getChildNodes()[0].getExecutionUnit().getLocation() )
                } else {
                    // No, we need to use an explicit Hive staging LSchema ("LKM HBase to SQL SQOOP")
                    def hiveLSchema  = getHiveTechnology().getLogicalSchemas().find { it.getName().equals(lSchemaName) }
                    if (hiveLSchema == null)
                        throw new OdiKMException( "LKM Hive to SQL SQOOP: addHiveSourceTask: Cannot find logical schema \"${lSchemaName}\". Please correct KM option \"HIVE_STAGING_SCHEMA\"." )
                    def hiveLocation = new OdiLocationAdapter( hiveLSchema, physicalNode.getContext() )
                    aAST.setSourceLocation( hiveLocation )
                }
                def taskContainer = this.getTaskContainer()
                def targetName = physicalNode.getName()
                def aTask = taskContainer.convertASTToTask(aAST, targetName, taskName, physicalNode.getExecutionUnit())
                aTask.getLine().setLineType(IProcedureLine.LineType.valueOf( lineType ))
                taskContainer.addTask(aTask)
            }

            void addHiveUnloadTask( SqlQuery sourceQuery, String lSchemaName ) {
                // Add task for Hive unload         
                // TODO: We should use the template here. But this causes an NPE.
                // """{# INCLUDE = 'LkmHiveToSql_UnloadHiveData' #}"""                
                addHiveSourceTask( "LKM Hive to SQL SQOOP", "Unload Hive data", """INSERT OVERWRITE DIRECTORY '\$[sourceDir]' ${sourceQuery.getText()}""", 250, "EX_UNIT_MAIN", lSchemaName )
            }

            void addHivePrepareSessionTask( String lSchemaName ) {
                // Add task for Hive unload         
                def kmName       = "LKM Hive to SQL SQOOP"
                def order        = 240
                def taskName     = "Prepare Hive Session"
                // TODO: We should use the template here. But this causes an NPE.
                //def templateText = '''{# INCLUDE = 'PrepareHiveSessionSource' #}'''
                def templateText = '''

      def executeStatement( def stmt, String cmd ) {
          def curMsg = (odiRef.getSummaryMessage()==null) ? "" : odiRef.getSummaryMessage()
          def newMsg = curMsg + "Submitting: " + cmd + "\\n"
          odiRef.setSummaryMessage( newMsg )
          stmt.execute( cmd )
          newMsg = newMsg + "Submitted OK\\n"
          odiRef.setSummaryMessage( newMsg )
      }

def con = odiRef.getJDBCConnection( "SRC" )
String sExtraJars = odiRef.getDataServerProperty( "HIVE_SESSION_JARS", "SRC" )

if (con == null) 
    throw new OdiKMException("ODIKM-HIVE-10000", "ODIKM-HIVE-10000: Failure to open JDBC connection. Please test DataServer connection." );

// Set session parameters for dynamic partitions
def stmt = con.createStatement()
executeStatement( stmt, """set hive.exec.dynamic.partition=true""" )
executeStatement( stmt, """set hive.exec.dynamic.partition.mode=nonstrict""" )
executeStatement( stmt, """set hive.enforce.bucketing=true""" )
executeStatement( stmt, """set hive.optimize.bucketmapjoin=true""" )
//executeStatement( stmt, """set mapred.job.reuse.jvm.num.tasks=-1""" )

// Add custom jars to this Hive session
if (sExtraJars != "") {
    def lstExtraJars = sExtraJars.split( ":" )
    for (j in lstExtraJars) {
        executeStatement( stmt, """add jar """ + j )
    }
}
stmt = null
con.close()
con = null
'''

                def aAST =  new CustomAST( kmName, templateText )                
                aAST.setOrder( order )
                aAST.setTargetTechnology( getGroovyTechnology() )

                def hiveLSchema  = getHiveTechnology().getLogicalSchemas().find { it.getName().equals(lSchemaName) }
                if (hiveLSchema == null)
                	throw new OdiKMException( "LKM Hive to SQL SQOOP: addHiveLogical: Cannot find logical schema \"${lSchemaName}\". Please correct KM option \"HIVE_STAGING_SCHEMA\"." )
                def hiveLocation = new OdiLocationAdapter( hiveLSchema, physicalNode.getContext() )
                aAST.setSourceLocation( hiveLocation )

                def taskContainer = this.getTaskContainer()
                def targetName = physicalNode.getName()
                def aTask = taskContainer.convertASTToTask(aAST, targetName, taskName, physicalNode.getExecutionUnit())             
                aTask.getLine().setLineType(IProcedureLine.LineType.valueOf( "MAP_BEGIN" ))
                taskContainer.addTask(aTask)
            }  

            void defineSqlStagingTable() {
                // Create DDL for staging table
                def stagingTable = getStagingTable()
                stgTableAST = new SqlCreateTableStatement( "abc", "def", stagingTable, destTechno, ffFinder )
                def stgTableColumnList = physicalNode.getPhysicalColumns().collect { physCol -> return Column.getColumn( stagingTable, physCol) }               
                stgTableAST.setColumns( stgTableColumnList )                
            }

            @ASTProducerMethod(processingType=ProcessingType.TARGET, producedASTType=HiveSqoopInsert.class, styleKey="ProcessingStyleKey.SQL.INSERT")
            public AbstractSyntaxTree INSERT( SqlQuery input ) {
                // Include all source-side expressions into the query AST
                // and save it to generate the Hive unload command
                sourceQuery = getTgtSelectQuery( input )
//                addHiveUnloadTask( sourceQuery, null )
                mBinding.setVariable( "HiveUnloadQuery", sourceQuery )  
                // Create DDL for staging table
                defineSqlStagingTable()

                // Publish variables into binding to make them available in templates and variableDefText
                updateBinding()

                HiveSqoopInsert result = new HiveSqoopInsert()
                result.setRawSourceDir(mBinding.getVariable("unquotedSourceDir"))  // The value of sourceDir was set in updateBinding().
                result.setHiveUnloadQuery(sourceQuery)
                return result
            } 

        } // End of class definition


import oracle.odi.km.exception.OdiKMException;
hiveLSchema = '';
// Find the hive staging logical schema, if any, otherwise use the execution unit logical schema as the hive logical schema. 
hiveStagingSchemaName = physicalNode.getKMOptionValueString( "HIVE_STAGING_LSCHEMA" )
if (hiveStagingSchemaName == null || hiveStagingSchemaName.length() == 0) {
  hiveLSchema = physicalNode.getChildNodes()[0].getExecutionUnit().getLogicalSchemaList().get(0)
} else {
  def hiveTechnology = physicalNode.getAdapter().findModelObject(ITechnology.class.getName(), null, "Hive")
  // No, we need to use an explicit Hive staging LSchema ("LKM HBase to SQL SQOOP")
  hiveLSchema  = hiveTechnology.getLogicalSchemas().find { it.getName().equals(hiveStagingSchemaName) }
  if (hiveLSchema == null)
    throw new OdiKMException( "LKM Hive to SQL SQOOP: addHiveSourceTask: Cannot find logical schema \"${hiveStagingSchemaName}\". Please correct KM option \"HIVE_STAGING_SCHEMA\"." )
}


unquotedSourceDir = HIVE_SQOOP_INSERT.getSourceDir();
HiveUnloadQuery = HIVE_SQOOP_INSERT.getHiveUnloadQuery();
// Dynamically set the source logical schema for the task 
taskControl.setSourceLogicalSchema( hiveLSchema ) // Variable hiveLSchema is calculated in the KM-level variable definition script.
